mapper.select:
SELECT newsno, text, url, source, publish_date
FROM news
WHERE newsno = #{newsno}

mapper.update:
UPDATE news
SET text = #{text}, url = #{url}, source = #{source}, publish_date = #{publishDate}
WHERE newsno = #{newsno}

mapper.select:
SELECT newsno, text, url, source, publish_date, r FROM ( SELECT newsno, text, url, source, publish_date, rownum AS r FROM ( SELECT newsno, text, url, source, publish_date FROM news
WHERE UPPER(text) LIKE '%' || UPPER(#{keyword}) || '%' OR UPPER(source) LIKE '%' || UPPER(#{keyword}) || '%'
ORDER BY publish_date DESC ) ) WHERE r BETWEEN #{start} AND #{end}
