mapper.select:
SELECT newscateno, genre, name, cnt, seqno, visible, rdate, newscategrpno 
FROM newscate 
WHERE newscateno = #{newscateno}

mapper.update:
UPDATE newscate 
SET seqno = seqno - 1  
WHERE newscateno = #{newscateno}

UPDATE newscate 
SET seqno = seqno + 1  
WHERE newscateno = #{newscateno}
